const navButton=document.querySelector(".nav-button"),mobileMenu=document.querySelector(".nav-mobile"),navBar=document.querySelector(".nav"),topButton=document.querySelector(".top-button"),loginButton=document.querySelector(".btn"),modal=document.querySelector(".modal"),menuOverlay=document.querySelector(".overlay"),menuClose=document.querySelector(".nav-mobile-container");window.addEventListener("scroll",()=>{document.body.scrollTop>50||document.documentElement.scrollTop>50?(navBar.classList.add("nav-fixed"),topButton.classList.add("top-active")):(navBar.classList.remove("nav-fixed"),topButton.classList.remove("top-active"))}),topButton.addEventListener("click",()=>{document.body.scrollTop=0,document.documentElement.scrollTop=0}),navButton.addEventListener("click",()=>{navButton.classList.toggle("close"),mobileMenu.classList.toggle("active"),menuOverlay.classList.toggle("overlay-active")}),loginButton.addEventListener("click",()=>{modal.classList.toggle("modal-active")}),window.addEventListener("click",e=>{e.target==menuClose&&(mobileMenu.classList.remove("active"),navButton.classList.remove("close"),menuOverlay.classList.remove("overlay-active"),console.log(e.target))}),window.addEventListener("click",e=>{e.target==menuOverlay&&(mobileMenu.classList.remove("active"),navButton.classList.remove("close"),menuOverlay.classList.remove("overlay-active"),console.log(e.target))}),window.addEventListener("click",e=>{e.target==modal&&modal.classList.remove("modal-active")});